
$(document).ready(function(){$('.burger').click(function(event){$(".burger-line").toggleClass('burger-line__active');$(".header__list").toggleClass('header__list__active');});});const anchors=document.querySelectorAll('a[href*="#"]')
for(let anchor of anchors){anchor.addEventListener("click",function(event){event.preventDefault();const blockID=anchor.getAttribute('href')
document.querySelector(''+blockID).scrollIntoView({behavior:"smooth",block:"start"})})}
